var Charon=angular.module("Charon",[]);Charon.controller("Login",function($scope,$http,$location){$scope.name="",$scope.pass="",$scope.login_attempt=function(){var data={name:$scope.name,pass:$scope.pass,key:sha256($scope.pass)};localStorage.setItem("key",data.key),$scope.error="";var enc_data=encrypt(data,get_cookie("PHPSESSID"));$http.post("/login",enc_data).success(function(data){location.reload()}).error(function(result,code){$scope.error=result})}});